import React from 'react';
import './signin.css';

class Signin extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			signInEmail: '',
			signInPassword: ''
		}
	}

	onEmailChange = (event) => {
		this.setState({signInEmail: event.target.value})
	}

	onPasswordChange = (event) => {
		this.setState({signInPassword: event.target.value})
	}

	onSubmitSignIn = () => {
		fetch('http://localhost:3000/signin', {
			method: 'post',
			headers: {'Content-Type': 'application/json'},
			body: JSON.stringify({
				email: this.state.signInEmail,
				password: this.state.signInPassword
			})
		})
		    .then(response => response.json())
		    .then(user => {
		  	  if(user.id) {
		  		this.props.loadUser(user)
		  		this.props.onRouteChange('home');
		  	  }
		    })
	}

	render() {
	  const { onRouteChange } = this.props;
	  return (
	    <div class="cont_principal">
		  <div class="cont_centrar">
		  <div class="cont_login">
		    <form action="">
		    <div class="cont_tabs_login">
		      <ul class='ul_tabs'>
		        <li class="active"><a href="#signin" onclick={() => onRouteChange('signin') }>SIGN IN</a>
		        <span class="linea_bajo_nom"></span>
		        </li>
		        <li><a href="#up" onclick= {() => onRouteChange('register') }>REGISTER</a><span class="linea_bajo_nom"></span>
		        </li>
		      </ul>
		      </div>
		  <div class="cont_text_inputs">

		    <input 
		    	type="text" 
		    	class="input_form_sign d_block active_inp" 
		    	placeholder="EMAIL" 
		    	name="email_us" 
		    	onChange={this.onEmailChange}
		    />

		    <input 
		    	type="password" 
		    	class="input_form_sign d_block  active_inp" 
		    	placeholder="PASSWORD" 
		    	name="pass_us" 
		    	onChange={this.onPasswordChange}
		    />  

		    <a href="#forgot" class="link_forgot_pass d_block" >Forgot Password ?</a>    
		<div class="terms_and_cons d_none">

		    </div>
		      </div>
		<div class="cont_btn">
		     <button class="btn_sign" onClick={this.onSubmitSignIn}>SIGN IN</button>
		      </div>
		    </form>
		    </div>
		  </div>
		</div>
	  );
	}
}

export default Signin;
