import React from 'react';
import './deals.css';
import DealEdit from './DealEdit/dealedit.js';

class Deals extends React.Component {
	constructor(props) {
		super(props);

		this.state = {
			dealsdata: [],
			isLoading: true,
			dealSelected: false,
			dealid: null,
			dealadd: false,
		}
	}

	fetchDeals() {
		fetch('http://localhost:3000/deals', {
			method: 'get',
			headers: {'Content-Type': 'application/json'},
		})
		    .then(response => response.json())
		    .then(deals => {
		  	    this.setState({
		  	    	dealsdata: deals,
		  	    	isLoading: false,
		  	    })
		  	})
	}

	componentDidMount() {
		this.fetchDeals();
	}




	onEditDeal() {
		fetch('http://localhost:3000/deals/:id', {
				method: 'get',
				headers: {'Content-Type': 'application/json'},
				body: JSON.stringify({
					id: this.state.dealid,
				})
		})
		    .then(response => response.json())
		    .then(user => {
		  	  if(user.id) {
		  	  	this.setState({ signInError: false})
		  		this.props.loadUser(user)
		  		this.props.onRouteChange('deals/:id');
		  	  } else {
		  	  	this.setState({ signInError: true })
		  	  	// this.props.onRouteChange('signin');
		  	  }
		    })
	}

	render() {
		if (this.state.dealadd) {
			return (
				<div className="container">HI?
				  	<DealEdit />
				</div>
			);
		} else {
			return (
				<div className="container">
					<div className="dealslist">
						<input className="btn_add" onClick={() => this.setState({dealadd: true})} type="button" value="ADD DEAL" />
						<table>
							<thead>
								<tr>
									<th>Tenant</th>
									<th>Property</th>
									<th>Unit</th>
								</tr>
							</thead>
							<tbody>
								{
									this.state.dealsdata.map(row => (
										<tr>
											<td>{row.tenant}</td>
											<td>{row.property}</td>
											<td>{row.unit}</td>
										</tr>
									))
								}  
							</tbody>
						</table>
					</div>
				</div>
			);
		}
	}
}

export default Deals;
