import React from 'react';
import './addnegotiationmodal.css';
import Select from 'react-select';
import {css} from 'emotion';
import moment from "moment";

function validate(propertyname, 
                  unit,
                  gla, 
                  status, 
                  cdate,
                  fdate, 
                  rent1, 
                  rent1end,
                  ti,
                  intcomm,
                  extcomm,
                  llw,
                  freerent,
                  ) { 
  return {
    propertyname: (propertyname.length === 0),
    unit: (unit.length === 0),
    gla: (gla.length === 0),
    status: (status.length === 0),
    cdate: (cdate.length === 0),
    fdate: (fdate.length === 0),
    rent1: (rent1.length === 0),
    rent1end: (rent1end.length === 0),
    ti: (ti.length === 0),
    intcomm: (intcomm.length === 0),
    extcomm: (extcomm.length === 0),
    llw: (llw.length === 0),
    freerent: (freerent.length === 0),
  };
}

class AddNegotiationModal extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      modalcategory: {
        thespace: true,
        timing: false,
        financialterms: false,
      },
      unit: '',
      propertyname: '',
      gla: '',
      status: '',
      cdate: '',
      fdate: '',
      rent1: '',
      rent1end: '',
      ti: '',
      intcomm: '',
      extcomm: '',
      llw: '',
      freerent: '',
      gfrent: '',
      gfrentstart: '',
      gfrentend: '',
      nfrent: '',
      nfrentstart: '',
      nfrentend: '',

      touched: {
        unit: false,
        propertyname: false,
        gla: false,
        status: false,
        cdate: false,
        fdate: false,
        rent1: false,
        rent1end: false,
        ti: false,
        intcomm: false,
        extcomm: false,
        llw: false,
        freerent: false,
      },
      propertyDropdown: [],
      unitDropdown: [],
      llbrokerDropdown: [],
      ttbrokerDropdown: [],
      propertyLoaded: false,
      loaded: false,
    }
    this.handleDateChange = this.handleDateChange.bind(this);
  }

  async componentDidMount() {

    await fetch('http://localhost:3000/adddealmodal/property', {
      method: 'get',
      headers: {'Content-Type': 'application/json'},
    })
      .then(response => response.json())
      .then(properties => {
        properties.forEach(element => {
          let dropDownEle = { label: element["propertyname"], value: element };
          this.state.propertyDropdown.push(dropDownEle);
        });      
      })
    this.setState({loaded: true})

  }

  onTenantChange = (event) => {
    this.setState({tenant: event.value.tenantname})
    console.log(this.state.tenant)
  }

  onPropertyChange = (event) => {
    this.setState({
      property: event.value.propertyname,
    })

      fetch('http://localhost:3000/adddealmodal/unit', {
        method: 'post',
        headers: {'Content-Type': 'application/json'},
        body: JSON.stringify({
          property: event.value.propertyname,
        })
      })
        .then(response => response.json())
        .then(units => {
          units.forEach(element => {
            let dropDownEle = { label: element["unit"], value: element };
            this.state.unitDropdown.push(dropDownEle);
          });  
          this.setState({propertyLoaded: true})
        })

  }

  onUnitChange = (event) => {
    this.setState({unit: event.value.unit})
  }

  onLLBrokerChange = (event) => {
    this.setState({llbroker: event.value.brokername})
  }

  onTTBrokerChange = (event) => {
    this.setState({ttbroker: event.value.brokername})
  }

  handleBlur = (field) => (event) => {
      this.setState({
        touched: { ...this.state.touched, [field]: true},
      });   
  }

  handleDateChange(date) {
    this.setState({
      cdate: date
    });
  }

  canBeNextSpace() {
    const errors = validate(this.state.unit, this.state.property, this.state.gla);
    const isSpaceDisabled = Object.keys(errors).some(x => errors[x]);
    return !isSpaceDisabled;
  }

  canBeSubmitted() {
    const errors = validate(this.state.tenant, this.state.unit, this.state.property, this.state.llbroker, this.state.ttbroker);
    const isDisabled = Object.keys(errors).some(x => errors[x]);
    return !isDisabled;
  }


  handleSubmit = (event) => {
    if (!this.canBeSubmitted()) {
      this.setState({
        touched: { ...this.state.touched, 'tenant': true, 'property': true, 'unit': true, 'llbroker': true, 'ttbroker': true}
      })
      event.preventDefault();
      return;

    }

    fetch('http://localhost:3000/dealsedit', {
      method: 'post',
      headers: {'Content-Type': 'application/json'},
      body: JSON.stringify({
        tenant: this.state.tenant,
        property: this.state.property,
        unit: this.state.unit,
        llbroker: this.state.llbroker,
        ttbroker: this.state.ttbroker,
      })
    })
      .then(response => response.json())
      .then(deal => {
        if(deal) {
          this.props.handleNegNoAdd();
        } else { console.log("uhoh")}
      })
    this.stateClear();
  }

  handleNext = () => {
    if(this.state.modalcategory['thespace']) {
      this.setState({
        modalcategory: { ...this.state.modalcategory, 'timing': true, 'thespace': false}
      })
    } else if (this.state.modalcategory['timing']) {
      this.setState({
        modalcategory: { ...this.state.modalcategory, 'financialterms': true, 'timing': false}
      })
    }
  }

  handleBack = () => {
    if(this.state.modalcategory['timing']) {
      this.setState({
        modalcategory: { ...this.state.modalcategory, 'timing': false, 'thespace': true}
      })
    } else if (this.state.modalcategory['financialterms']) {
      this.setState({
        modalcategory: { ...this.state.modalcategory, 'financialterms': false, 'timing': true}
      })
    }
  }

  stateClear = () => {
    this.setState({
      touched: false,
      tenant: '',
      unit: '',
      property: '',
      llbroker: '',
      ttbroker: '',
    });
  }

  render() {
    const errors = validate(this.state.tenant, this.state.unit, this.state.property, this.state.llbroker, this.state.ttbroker);
    const isDisabled = Object.keys(errors).some(x => errors[x]);
    const shouldMarkError = (field) => {
      return errors[field] && this.state.touched[field];
    }

    const showHideClassName = this.props.show ? 'modal display-block' : 'modal display-none';
    return (
      <div className={showHideClassName}>
        <React.Fragment>
         { this.state.loaded && (
           <div className='modal-main'>
              <div className="modal-title">Add New Stage of Negotiation</div>
              <div className="modal-body">
              <div className="modal-body-header">
                    <p className={ this.state.modalcategory['thespace'] ? "header-sub active-sub" : "header-sub"}>1. The Space</p>
                    <p className={ this.state.modalcategory['timing'] ? "header-sub active-sub" : "header-sub"}>2. Timing</p>
                    <p className={ this.state.modalcategory['financialterms'] ? "header-sub active-sub" : "header-sub"}>3. Financial Terms</p>
                  </div>
                { this.state.modalcategory['thespace'] ? (
                  <React.Fragment>

                  <div className="boxcontainer">
                    <div className="leftbox">
                      <p className={shouldMarkError('property') ? "input-title-error" : "input-title"}>Property:</p>
                      <Select 
                        options={this.state.propertyDropdown}
                        className="form-input"
                        classNamePrefix="form-input"
                        onChange={this.onPropertyChange.bind(this)}
                        onBlur={this.handleBlur('property')}
                      />

                    { this.state.propertyLoaded ? ([
                        <p className={shouldMarkError('unit') ? "input-title-error" : "input-title"}>Unit:</p>,
                        <Select 
                          options={this.state.unitDropdown}
                          className="form-input"
                          classNamePrefix="form-input"
                          onChange={this.onUnitChange.bind(this)}
                          onBlur={this.handleBlur('unit')}
                        />
                      ]) : null }
                    </div>
                    <div className="rightbox">
                      <p className={shouldMarkError('gla') ? "input-title-error" : "input-title"}>GLA:</p>
                      <input 
                        type="input"
                        className="form-input"
                        onChange={this.onLLBrokerChange.bind(this)}
                        onBlur={this.handleBlur('llbroker')}
                      />
                    </div>
                  </div>
                  </React.Fragment>
                ) : this.state.modalcategory['timing'] ? (
                  <React.Fragment>
                  <div className="boxcontainer">
                    <div className="leftbox">
                      <p className={shouldMarkError('cdate') ? "input-title-error" : "input-title"}>Commencement Date: (mm/dd/yyyy)</p>
                      <input
                        type="input"
                        className="form-input"
                      />
                      <p className={shouldMarkError('fdate') ? "input-title-error" : "input-title"}>Fixturing Start Date: (mm/dd/yyyy)</p>
                      <input
                        type="input"
                        className="form-input"
                      />
                    </div>
                    <div className="rightbox">
                      <p className={shouldMarkError('term') ? "input-title-error" : "input-title"}>Term (months):</p>
                      <input
                        type="input"
                        className="form-input"
                      />
                      <p className={shouldMarkError('expirydate') ? "input-title-error" : "input-title"}>Expiry Date: (mm/dd/yyyy)</p>
                      <input
                        type="input"
                        className="form-input"
                      />
                    </div>
                  </div>
                  </React.Fragment>
                  ) : (
                  <React.Fragment>
                  <div className="boxcontainer">
                    <div className="leftbox">
                      <p className={shouldMarkError('rent') ? "input-title-error" : "input-title"}>Rent:</p>
                      <input
                        type="input"
                        className="form-input"
                      />
                      <p className={shouldMarkError('ti') ? "input-title-error" : "input-title"}>TI PSF:</p>
                      <input
                        type="input"
                        className="form-input"
                      />
                    </div>
                    <div className="rightbox">
                      <p className={shouldMarkError('llw') ? "input-title-error" : "input-title"}>LLW:</p>
                      <input
                        type="input"
                        className="form-input"
                      />
                      <p className={shouldMarkError('extcomm') ? "input-title-error" : "input-title"}>External Commission:</p>
                      <input
                        type="input"
                        className="form-input"
                      />
                      <p className={shouldMarkError('intcomm') ? "input-title-error" : "input-title"}>External Commission:</p>
                      <input
                        type="input"
                        className="form-input"
                      />
                      <p className={shouldMarkError('gfrent') ? "input-title-error" : "input-title"}>Gross Free Rent:</p>
                      <input
                        type="input"
                        className="form-input"
                      />
                    </div>
                  </div>
                  </React.Fragment>
                  )}


                  <div className="line"></div>
                  <div className="buttons">
                    <div className="left">
                      { !this.state.modalcategory['thespace'] && (
                        <input 
                          type="back"
                          value="Back" 
                          className="button cancel-button float-left"
                          onClick={ (event) => this.handleBack(event)}
                        />
                      )}
                    </div>
                    <div className="right">

                      { isDisabled ? (
                        <input 
                          type="next"
                          value="Next" 
                          className="button add-button-error float-right"
                          onClick={ (event) => this.handleNext(event)}
                        />
                      ) : (
                        <input 
                          type="submit" 
                          className="button add-button float-right"
                          onClick={ (event) => this.handleSubmit(event)}
                        />
                      )}

                      <input
                        type="cancel"
                        value="Cancel"
                        className="button cancel-button float-right"
                        onClick={ (event) => { this.props.handleNegNoAdd(); this.stateClear(); }}
                      />
                    </div>
                  </div>
              </div>
          </div>
         )}
        </React.Fragment>
      </div>
    );
  }
};

export default AddNegotiationModal;
