import React from 'react';
import './addpropertymodal.css';
import Select from 'react-select';
import {css} from 'emotion';

function validate(propertyname, address, city, province, rentablearea, storeys) { 
  return {
    propertyname: (propertyname.length === 0),
    address: (address.length === 0),
    city: (city.length === 0),
    province: (province.length === 0),
    rentablearea: (rentablearea.length === 0),
    storeys: (storeys.length === 0),
  };
}

class AddPropertyModal extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      propertyname: '',
      address: '',
      city: '',
      province: '',
      rentablearea: '',
      storeys: '',
      siteareasf: '',
      yearbuilt: '',
      yearacquired: '',
      touched: {
        propertyname: false,
        address: false,
        city: false,
        province: false,
        rentablearea: false,
        storeys: false,
        siteareasf: false,
        yearbuilt: false,
        yearacquired: false,
      },
      provinceDropdown: ['BC','ON','AB'],
      propertyLoaded: false,
      loaded: false,
    }
  }

  onChange = name => event => {
    this.setState({ [name]: event.target.value});
  }

  onProvinceChange = (event) => {
    this.setState({province: event.value.province})
  }

  handleBlur = (field) => (event) => {
      this.setState({
        touched: { ...this.state.touched, [field]: true},
      });   
  }

  canBeSubmitted() {
    const errors = validate(this.state.propertyname, this.state.address, this.state.city, this.state.province, this.state.rentablearea, this.state.storeys);
    const isDisabled = Object.keys(errors).some(x => errors[x]);
    return !isDisabled;
  }

  handleSubmit = (event) => {
    if (!this.canBeSubmitted()) {
      this.setState({
        touched: { ...this.state.touched, 'propertyname': true, 'city': true, 'address': true, 'province': true, 'rentablearea': true, 'storeys': true}
      })
      event.preventDefault();
      return;

    }

    fetch('http://localhost:3000/dealsedit', {
      method: 'post',
      headers: {'Content-Type': 'application/json'},
      body: JSON.stringify({
        propertyname: this.state.propertyname,
        city: this.state.city,
        address: this.state.address,
        province: this.state.province,
        rentablearea: this.state.rentablearea,
      })
    })
      .then(response => response.json())
      .then(deal => {
        if(deal) {
          this.props.handlecityNoAdd();
        } else { console.log("uhoh")}
      })
    this.stateClear();
  }

  stateClear = () => {
    this.setState({
      touched: false,
      propertyname: '',
      address: '',
      city: '',
      province: '',
      rentablearea: '',
      storeys: '',
      siteareasf: '',
      yearbuilt: '',
      yearacquired: '',
    });
  }

  render() {
    const errors = validate(this.state.propertyname, this.state.address, this.state.city, this.state.province, this.state.rentablearea);
    const isDisabled = Object.keys(errors).some(x => errors[x]);
    const shouldMarkError = (field) => {
      return errors[field] && this.state.touched[field];
    }

    const showHideClassName = this.props.show ? 'deal-modal deal-display-block' : 'deal-modal deal-display-none';
    return (
      <div className={showHideClassName}>
        <React.Fragment>
        <section className='deal-modal-main'>
         { this.state.loaded && (
           <React.Fragment>
            <div className="deal-modal-title">Add New Deal</div>,
            <div className="deal-modal-body">

              <p className={shouldMarkError('propertyname') ? "deal-input-title-error" : "deal-input-title"}>propertyname Name:</p>
              <Select 
                options={this.state.propertynameDropdown}
                className="deal-form-input"
                classNamePrefix="deal-form-input"
                onChange={this.onpropertynameChange.bind(this)}
                onBlur={this.handleBlur('propertyname')}
              />

              <p className={shouldMarkError('city') ? "deal-input-title-error" : "deal-input-title"}>city:</p>
              <Select 
                options={this.state.cityDropdown}
                className="deal-form-input"
                classNamePrefix="deal-form-input"
                onChange={this.oncityChange.bind(this)}
                onBlur={this.handleBlur('city')}
              />

              { this.state.cityLoaded ? ([
                  <p className={shouldMarkError('address') ? "deal-input-title-error" : "deal-input-title"}>address:</p>,
                  <Select 
                    options={this.state.addressDropdown}
                    className="deal-form-input"
                    classNamePrefix="deal-form-input"
                    onChange={this.onaddressChange.bind(this)}
                    onBlur={this.handleBlur('address')}
                  />
                ]) : null }

              <p className={shouldMarkError('province') ? "deal-input-title-error" : "deal-input-title"}>Landlord Broker:</p>
              <Select 
                options={this.state.provinceDropdown}
                className="deal-form-input"
                classNamePrefix="deal-form-input"
                onChange={this.onprovinceChange.bind(this)}
                onBlur={this.handleBlur('province')}
              />

              <p className={shouldMarkError('rentablearea') ? "deal-input-title-error" : "deal-input-title"}>propertyname Broker:</p>
              <Select 
                options={this.state.rentableareaDropdown}
                className="deal-form-input"
                classNamePrefix="deal-form-input"
                onChange={this.onrentableareaChange.bind(this)}
                onBlur={this.handleBlur('rentablearea')}
              />

              <div className="line"></div>
              <div className="buttons">

              { isDisabled ? (
                <input 
                  type="submit" 
                  className="button add-button-error"
                  onClick={ (event) => this.handleSubmit(event)}
                />
              ) : (
                <input 
                  type="submit" 
                  className="button add-button"
                  onClick={ (event) => this.handleSubmit(event)}
                />
              )}

              <input
                type="cancel"
                value="Cancel"
                readOnly
                className="button cancel-button"
                onClick={ (event) => { this.props.handlePropertyNoAdd(); this.stateClear(); }}
              />

              </div>
            </div>
        </React.Fragment>
       )}
        </section>
        </React.Fragment>
      </div>
    );
  }
};

export default AddPropertyModal;
