import React from 'react';
import './adddealmodal.css';
import Select from 'react-select';

function validate(tenant, unit, property, llbroker, ttbroker) { 
  return {
    tenant: (tenant.length === 0),
    unit: (unit.length === 0),
    property: (property.length === 0),
    llbroker: (llbroker.length === 0),
    ttbroker: (ttbroker.length === 0),
  };
}

class AddDealModal extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      tenant: '',
      unit: '',
      property: '',
      llbroker: '',
      ttbroker: '',
      touched: {
        tenant: false,
        unit: false,
        property: false,
        llbroker: false,
        ttbroker: false
      },
      tenantDropdown: [],
      propertyDropdown: [],
      unitDropdown: [],
      llbrokerDropdown: [],
      ttbrokerDropdown: [],

    }
  }

  componentDidMount() {
    let initTenantDropdown: [];
    let initPropertyDropdown: [];
    let initUnitDropdown: [];
    let initLLbrokerDropdown: [];
    let initTTbrokerDropdown: [];
    fetch('http://localhost:3000/adddealmodal/tenant', {
      method: 'get',
      headers: {'Content-Type': 'application/json'},
    })
      .then(response => response.json())
      .then(tenants => {
        initTenantDropdown = tenants.results.map((tenant) => {
          return tenant
        });
        this.setState({
          tenantDropdown: initTenantDropdown,
        })
      })
  }

  onTenantChange   = (event) => {
    this.setState({tenant: event.target.value})
  }

  onUnitChange   = (event) => {
    this.setState({unit: event.target.value})
  }

  onPropertyChange   = (event) => {
    this.setState({property: event.target.value})
  }

  onLLBrokerChange   = (event) => {
    this.setState({llbroker: event.target.value})
  }

  onTTBrokerChange   = (event) => {
    this.setState({ttbroker: event.target.value})
  }

  handleBlur = (field) => (event) => {
      this.setState({
        touched: { ...this.state.touched, [field]: true},
      });   
  }

  canBeSubmitted() {
    const errors = validate(this.state.tenant, this.state.property, this.state.unit, this.state.llbroker, this.state.ttbroker);
    const isDisabled = Object.keys(errors).some(x => errors[x]);
    return !isDisabled;
  }

  handleSubmit = (event) => {
    if (!this.canBeSubmitted()) {
      this.setState({
        touched: { ...this.state.touched, 'tenant': true, 'property': true, 'unit': true, 'llbroker': true, 'ttbroker': true}
      })
      event.preventDefault();
      return;

    }

    fetch('http://localhost:3000/dealsedit', {
      method: 'post',
      headers: {'Content-Type': 'application/json'},
      body: JSON.stringify({
        tenant: this.state.tenant,
        property: this.state.property,
        unit: this.state.unit,
        llbroker: this.state.llbroker,
        ttbroker: this.state.ttbroker,
      })
    })
      .then(response => response.json())
      .then(deal => {
        if(deal) {
          this.props.handleDealNoAdd();
        } else { console.log("uhoh")}
      })
    this.stateClear();
  }

  stateClear = () => {
    this.setState({
      touched: false,
      tenant: '',
      unit: '',
      property: '',
      llbroker: '',
      ttbroker: '',
    });
  }

  render() {
    const errors = validate(this.state.tenant, this.state.unit, this.state.property, this.state.llbroker, this.state.ttbroker);
    const isDisabled = Object.keys(errors).some(x => errors[x]);
    const shouldMarkError = (field) => {
      return errors[field] && this.state.touched[field];
    }

    const showHideClassName = this.props.show ? 'modal display-block' : 'modal display-none';
    return (
      <div className={showHideClassName}>
        <section className='modal-main'>
          <div className="modal-title">
            Add New Deal
          </div>
          <div className="modal-body">
            <p className="input-title">Tenant Name:</p>
            <Select options={this.state.tenantDropdown}
              onChange={this.onTenantChange}
              onBlur={this.handleBlur('tenant')}
            />
            { shouldMarkError('tenant') ? (
              <p className="input-error-text">Please provide a tenant name.</p>
              ) : null
            }

            <p className="input-title">Property:</p>
            <input 
              type="text" 
              className={ shouldMarkError('property') ? "form-input input-error" : "form-input"}
              name="property" 
              value={this.state.property}
              onChange={this.onPropertyChange}
              onBlur={this.handleBlur('property')}
            />  
            { shouldMarkError('property') ? (
              <p className="input-error-text">Please provide a property.</p>
              ) : null
            }

            <p className="input-title">Unit:</p>
            <input 
              type="text" 
              className={ shouldMarkError('unit') ? "form-input input-error" : "form-input"}
              name="unit" 
              value={this.state.unit}
              onChange={this.onUnitChange}
              onBlur={this.handleBlur('unit')}
            />  
            { shouldMarkError('unit') ? (
              <p className="input-error-text">Please provide a unit number.</p>
              ) : null
            }

            <p className="input-title">Landlord Broker:</p>
            <input 
              type="text" 
              className={ shouldMarkError('llbroker') ? "form-input input-error" : "form-input"}
              name="llbroker" 
              value={this.state.llbroker}
              onChange={this.onLLBrokerChange}
              onBlur={this.handleBlur('llbroker')}
            />    
            { shouldMarkError('llbroker') ? (
              <p className="input-error-text">Please provide a landlord broker name.</p>
              ) : null
            }

            <p className="input-title">Tenant Broker:</p>
            <input 
              type="text" 
              className={ shouldMarkError('ttbroker') ? "form-input input-error" : "form-input"}
              name="llbroker" 
              value={this.state.ttbroker}
              onChange={this.onTTBrokerChange}
              onBlur={this.handleBlur('ttbroker')}
            />   
            { shouldMarkError('ttbroker') ? (
              <p className="input-error-text">Please provide a tenant broker name.</p>
              ) : null
            }
            <div className="line"></div>
            <div className="buttons">

            { isDisabled ? (
              <input 
                type="submit" 
                value="ADD DEAL" 
                className="button add-button-error"
                onClick={ (event) => this.handleSubmit(event)}
              />
            ) : (
              <input 
                type="submit" 
                value="ADD DEAL" 
                className="button add-button"
                onClick={ (event) => this.handleSubmit(event)}
              />
            )}

            <input
              type="cancel"
              value="CANCEL"
              className="button cancel-button"
              onClick={ (event) => { this.props.handleDealNoAdd(); this.stateClear(); }}
            />

            </div>
          </div>
        </section>
      </div>
    );
  }
};

export default AddDealModal;
