import React, { Component } from 'react';
import './register.css';

function validate(firstName) { 
	console.log(firstName.length === 0);
	return {
		firstName: firstName.length === 0
	};
}

class Register extends Component {
	constructor(props) {
		super(props);
		this.state = {
			email: '',
			password: '',
			firstName: '',
			lastName: '',
			touched: {
				firstName: false,
				lastName: false,
				email: false,
				password: false,
				confirmpassword: false
			}
		}
	}

	onFirstNameChange = (event) => {
		this.setState({firstName: event.target.value})
	}

	onLastNameChange = (event) => {
		this.setState({lastName: event.target.value})
	}

	onEmailChange = (event) => {
		this.setState({email: event.target.value})
	}

	onPasswordChange = (event) => {
		this.setState({password: event.target.value})
	}

	onConfirmPWChange = (event) => {
		this.setState({confirmpassword: event.target.value})
	}

	onSubmitSignIn = () => {
		console.log(this.state.email);
		console.log(this.state.password);
		console.log(this.state.name);
		fetch('http://localhost:3000/register', {
			method: 'post',
			headers: {'Content-Type': 'application/json'},
			body: JSON.stringify({
				email: this.state.email,
				password: this.state.password,
				firstName: this.state.firstName,
				lastName: this.state.lastName
			})
		})
		  .then(response => response.json())
		  .then(user => {
		  	if(user) {
		  		this.props.loadUser(user)
		  		this.props.onRouteChange('home');
		  	} else {
		  		console.log(user);
		  	}
		  })
	}

	handleBlur = (field) => (event) => {
		this.setState({
			touched: { ...this.state.touched, [field]: true},
		});
		console.log(this.state.touched[field])
	}

	onFirstNameFocus = () => {
		this.setState({focusFirstName: true})
		console.log("focusFirstName set to ", this.state.focusFirstName);
	}

	handleFirstName = () => {
		//this.setState({validatingFirstName: true});
		if (this.state.focusFirstName && this.state.firstName === '') {
			this.setState({focusFirstName: false})
			return (
				<div>ERRORRRR</div>
				)
		} else {
			this.setState({focusFirstName: false})
		}
	}

	renderFirstNameError = () => {
		if (this.state.focusFirstName && this.state.firstName === '') {
			return (
				<div>ERRORRRRR</div>
				)

		}

	}

	handleConfirmPassword = () => {
		this.setState({validatingConfirmPassword: true});
	}

	renderConfirmPasswordError = () => {
		if (this.state.validatingFirstName && this.state.firstName === '') {
			return (
				<div>ERRORRRRR</div>
			)
		}
	}

	render() {
		const errors = validate(this.state.firstName);
		const shouldMarkError = (field) => {
			const hasError = errors[field];
			const shouldShow = this.state.touched[field];
			console.log("hasError set to ", errors[field]);
			console.log("should show set to ", this.state.touched[field]);
			return hasError ? shouldShow : false;
		}
		const { onRouteChange } = this.props;
		return (
		    <div className="cont_principal">
			  <div className="cont_centrar">
			    <div className="cont_login">
			        <div className="cont_tabs_login">
			          <ul className='ul_tabs'>
			            <li><p onClick={() => onRouteChange('signin') }>SIGN IN</p>
			              <span className="linea_bajo_nom"></span>
			            </li>
			            <li className="active">
			            <p onClick= {() => onRouteChange('register') }>REGISTER</p><span className="linea_bajo_nom"></span>
			            </li>
			          </ul>
			        </div>
			        <div className="cont_text_inputs">
			         	<input 
					    	type="text" 
					    	className={shouldMarkError('firstname') ? "error" : "input_form_sign d_block active_inp"}
					    	placeholder="FIRST NAME" 
					    	name="name_us" 
					    	onChange={this.onFirstNameChange}
					    	onBlur={this.handleBlur('firstname')}
					    />

					    <input 
					    	type="text" 
					    	className="input_form_sign d_block active_inp" 
					    	placeholder="LAST NAME" 
					    	name="name_us" 
					    	onChange={this.onLastNameChange}
					    />
					    <input 
					    	type="text" 
					    	className="input_form_sign d_block active_inp" 
					    	placeholder="EMAIL" 
					    	name="email_us" 
					    	onChange={this.onEmailChange}
					    />
					    <input 
					    	type="password" 
					    	className="input_form_sign d_block active_inp" 
					    	placeholder="PASSWORD" 
					    	name="pass_us" 
					    	onChange={this.onPasswordChange}
					    />  
					   <input 
					   		type="password" 
					   		className="input_form_sign d_block active_inp" 
					   		placeholder="CONFIRM PASSWORD" 
					   		name="conf_pass_us" 
					    	onChange={this.onConfirmPSChange}
					   	/>
			          </div>
					  <div className="cont_btn">
				        <input 
				            type="submit" 
				            value="REGISTER" 
				            className="btn_sign" 
				            onClick={this.onSubmitSignIn} 
			            />
			          </div>
			        </div>
			      </div>

			    </div>
		);
	}
}

export default Register;
