import React from 'react';
import './topnav.css';
import dashicon from './dashboard.png';
import pipeicon from './pipeline.png';
import reporticon from './reports.png';
import { DropdownMenu, MenuItem } from 'react-bootstrap-dropdown-menu';


class TopNav extends React.Component {
	constructor(props) {
	    super(props);

	    this.state = {
	    	showProfileMenu: false,
	    };
	    this.showProfileMenu = this.showMenu.bind(this);
    	this.closeProfileMenu = this.closeMenu.bind(this);
    }



	render() {
		const firstNameLetter = this.props.firstName.charAt(0).toUpperCase();
		const lastNameLetter = this.props.lastName.charAt(0).toUpperCase();
	    const avatarAbbrev = firstNameLetter.concat(lastNameLetter);

		return (
			<div className="nav">
			  <div className="top-nav">
			    <div className="search-bar">
			      <input type="text" placeholder="Search..." name="search" className="searchbox"/>
				</div>
				<div className="account">
				  <div className="avatar">{`${avatarAbbrev}`}</div>
					  <DropdownMenu userName='John Doe'>
				        <MenuItem text='Home' location='/showuser' />
				        <MenuItem text='Edit Profile' location='/showuser' />
				        <MenuItem text='Logout' location='/showuser' />
				      </DropdownMenu>
				  <div className="signout" onClick={() => this.props.onRouteChange('signout')}>
				    SIGN OUT    
				  </div>
				</div>
			  </div>
			  <div className="bottom-nav">
			   <div className="icons">
			   <a href="../App.js">
				    <div className="navicon">
					  <img src={ dashicon } alt="Home" height="60%" width="auto" />
					  <p className="icontext">HOME</p>
				    </div>
				</a>
				<a href="../App.js">
				    <div className="navicon">
					  <img src={ dashicon } alt="Portfolio" height="60%" width="auto" />
					  <p className="icontext">PORTFOLIOS</p>
				    </div>
				</a>
				<a href="../App.js">
				    <div className="navicon">
					  <img src={ pipeicon } height="60%" width="auto" alt="Properties" />
					   <p className="icontext">PROPERTIES</p>
				    </div>
				</a>
				<a href="../App.js">
				    <div className="navicon">
					  <img src={ dashicon } alt="Tenants" height="60%" width="auto" />
					  <p className="icontext">TENANTS</p>
				    </div>
				</a>
				<a href="../App.js">
				    <div className="navicon">
					  <img src={ reporticon } height="60%" width="auto" alt="Reports" />
					  <p className="icontext">REPORTS</p>
				    </div>
				</a> 
			   </div>
			  </div>
			</div>
		);
	}
}

export default TopNav;
