import React from 'react';
import './adddealmodal.css';


class AddDealModal extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      tenant: '',
      unit: '',
      property: '',
      llbroker: '',
      ttbroker: '',
      touched: {
        tenant: false,
        unit: false,
        property: false,
        llbroker: false,
        ttbroker: false
      }
    }
  }

  onTenantChange   = (event) => {
    this.setState({tenant: event.target.value})
    console.log(this.state.tenant);
  }

  onUnitChange   = (event) => {
    this.setState({unit: event.target.value})
    console.log(this.state.unit);
  }

  onPropertyChange   = (event) => {
    this.setState({property: event.target.value})
    console.log(this.state.property);
  }

  onLLBrokerChange   = (event) => {
    this.setState({llbroker: event.target.value})
    console.log(this.state.llbroker);
  }

  onTTBrokerChange   = (event) => {
    this.setState({ttbroker: event.target.value})
    console.log(this.state.ttbroker);
  }


  render() {
    const errors = validate(this.state.firstName, this.state.lastName, this.state.email, this.state.password, this.state.confirmPassword);
    const isDisabled = Object.keys(errors).some(x => errors[x]);
    const shouldMarkError = (field) => {
      return errors[field] && this.state.touched[field];
    }

    const showHideClassName = this.props.show ? 'modal display-block' : 'modal display-none';
    return (
      <div className={showHideClassName}>
        <section className='modal-main'>
          <div className="modal-title">
            Add New Deal
          </div>
          <div className="modal-body">
            <input 
              type="text" 
              className={ activeinput ? "form-input active" : "form-input"}
              name="tenant" 
              onChange={this.onTenantChange}
            />

            <input 
              type="text" 
              className={ activeinput ? "form-input active" : "form-input"}
              name="property" 
              onChange={this.onPropertyChange}
            />  

            <input 
              type="text" 
              className={active-input ? "form-input active" : "form-input"}
              name="unit" 
              onChange={this.onUnitChange}
            />  

            <input 
              type="text" 
              className={active-input ? "form-input active" : "form-input"}
              name="llbroker" 
              onChange={this.onLLBrokerChange}
            />    

            <input 
              type="text" 
              className="" 
              name="ttbroker" 
              onChange={this.onTTBrokerChange}
            />   

            <button onClick={this.props.handleDealNoAdd}>
              Cancel
            </button>
          </div>
        </section>
      </div>
    );
  }
};

export default AddDealModal;
