import React from 'react';
import './dealedit.css';

class DealEdit extends React.Component {
	constructor(props) {
		super(props);

		this.state = {
			tenant: '',
			property: '',
			unit: '',
		}
	}

	onTenantChange = (event) => {
		this.setState({tenant: event.target.value})
	}

	onPropertyChange = (event) => {
		this.setState({property: event.target.value})
	}

	onUnitChange = (event) => {
		this.setState({unit: event.target.value})
	}


	handleSubmit() {
		fetch('http://localhost:3000/deals/add', {
			method: 'post',
			headers: {'Content-Type': 'application/json'},
			body: JSON.stringify({
				email: this.state.email,
				password: this.state.password,
				firstName: this.state.firstName,
				lastName: this.state.lastName
			})
		})
		  .then(response => response.json())
		  /*.then(exists => {
		  	if(exists) {
		  		this.setState({userExists: true})
		  	}
		  })*/
		  .then(user => {
		  	if(user) {
		  		this.props.loadUser(user)
		  		this.props.onRouteChange('home');
		  	} 
		  })


		this.props.handleDealNoAdd();
	}

	render() {
		return (

			<div className="container"> 
			  <div className="dealslist">
			    <input 
			    	type="text" 
			    	className="plug" 
			    	placeholder="TENANT NAME" 
			    	name="tenant" 
			    	onChange={this.onTenantChange}
			    />

			    <input 
			    	type="text" 
			    	className="plug" 
			    	placeholder="PROPERTY" 
			    	name="property" 
			    	onChange={this.onPropertyChange}
			    />  
			     <input 
			    	type="text" 
			    	className="plug" 
			    	placeholder="UNIT" 
			    	name="unit" 
			    	onChange={this.onUnitChange}
			    />  

			    <p><input
			    	type="button"
			    	className="plugbutton"
			    	placeholder="Cancel"
			    	name="cancel"
			    	onClick= {this.handleSubmit}
			    /></p>
			    </div>
			</div>
		);
	}
}

export default DealEdit;
