import React from 'react';
import './deals.css';
import DealEdit from './DealEdit/dealedit.js';

class Deals extends React.Component {
	constructor(props) {
		super(props);

		this.state = {
			dealsdata: [],
			isLoading: true,
			dealSelected: false,
			dealid: null,
			dealadd: true,
		}
		this.handleDealAdd = this.handleDealAdd.bind(this);
	}

	fetchDeals() {
		fetch('http://localhost:3000/deals', {
			method: 'get',
			headers: {'Content-Type': 'application/json'},
		})
		    .then(response => response.json())
		    .then(deals => {
		  	    this.setState({
		  	    	dealsdata: deals,
		  	    	isLoading: false,
		  	    })
		  	})
	}

	componentDidMount() {
		this.fetchDeals();
	}



	handleDealAdd() {
		this.setState({dealadd: true});
		console.log(this.state.dealadd);
	}

	onEditDeal() {
		fetch('http://localhost:3000/deals/:id', {
				method: 'get',
				headers: {'Content-Type': 'application/json'},
				body: JSON.stringify({
					id: this.state.dealid,
				})
		})
		    .then(response => response.json())
		    .then(user => {
		  	  if(user.id) {
		  	  	this.setState({ signInError: false})
		  		this.props.loadUser(user)
		  		this.props.onRouteChange('deals/:id');
		  	  } else {
		  	  	this.setState({ signInError: true })
		  	  	// this.props.onRouteChange('signin');
		  	  }
		    })
	}

	render() {
		return (
			<div className="container">
			{ this.state.dealadd ? (
				<div>
				<input 
			    	type="text" 
			    	className="plug" 
			    	placeholder="TENANT NAME" 
			    	name="tenant" 
			    />

			    <input 
			    	type="text" 
			    	className="plug" 
			    	placeholder="ID" 
			    	name="id" 
			    />  
				</div>
			) : (
				<div className="dealslist">
					<input className="btn_add" onClick={this.handleDealAdd} type="button" value="ADD DEAL" />
					<table>
						<thead>
							<tr>
								<th>Tenant</th>
								<th>Property</th>
								<th>Unit</th>
							</tr>
						</thead>
						<tbody>
							{
								this.state.dealsdata.map(row => (
									<tr>
										<td>{row.tenant}</td>
										<td>{row.property}</td>
										<td>{row.unit}</td>
									</tr>
								))
							}  
						</tbody>
					</table>
				</div>
			)}
			</div>
		);
	}
}

export default Deals;
