import React from 'react';
import './register.css';

class Register extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			email: '',
			password: '',
			name: ''
		}
	}

	onNameChange = (event) => {
		this.setState({name: event.target.value})
	}

	onEmailChange = (event) => {
		this.setState({email: event.target.value})
	}

	onPasswordChange = (event) => {
		this.setState({password: event.target.value})
	}

	onConfirmPWChange = (event) => {
		this.setState({confirmpassword: event.target.value})
	}

	onSubmitSignIn = () => {
		fetch('http://localhost:3000/register', {
			method: 'post',
			headers: {'Content-Type': 'application/json'},
			body: JSON.stringify({
				email: this.state.email,
				password: this.state.password,
				name: this.state.name
			})
		})
		  .then(response => response.json())
		  .then(user => {
		  	if(user) {
		  		this.props.loadUser(user)
		  		this.props.onRouteChange('home');
		  	} else {
		  		console.log(user);
		  	}
		  })
	}

	render() {
		const { onRouteChange } = this.props;
		return (
		    <div className="cont_principal">
			  <div className="cont_centrar">
			    <div className="cont_login">
			        <div className="cont_tabs_login">
			          <ul className='ul_tabs'>
			            <li><p onClick={() => onRouteChange('signin') }>SIGN IN</p>
			              <span className="linea_bajo_nom"></span>
			            </li>
			            <li className="active">
			            <p onClick= {() => onRouteChange('register') }>REGISTER</p><span className="linea_bajo_nom"></span>
			            </li>
			          </ul>
			        </div>
			        <div className="cont_text_inputs">
					    <input 
					    	type="text" 
					    	className="input_form_sign d_block active_inp" 
					    	placeholder="NAME" 
					    	name="name_us" 
					    	onChange={this.onNameChange}
					    />
					    <input 
					    	type="text" 
					    	className="input_form_sign d_block active_inp" 
					    	placeholder="EMAIL" 
					    	name="email_us" 
					    	onChange={this.onEmailChange}
					    />
					    <input 
					    	type="password" 
					    	className="input_form_sign d_block active_inp" 
					    	placeholder="PASSWORD" 
					    	name="pass_us" 
					    	onChange={this.onPasswordChange}
					    />  
					   <input 
					   		type="password" 
					   		className="input_form_sign d_block active_inp" 
					   		placeholder="CONFIRM PASSWORD" 
					   		name="conf_pass_us" 
					    	onChange={this.onConfirmPSChange}
					   	/>
			          </div>
					  <div className="cont_btn">
				        <input 
				            type="submit" 
				            value="REGISTER" 
				            className="btn_sign" 
				            onChange={this.onSubmitSignIn} 
			            />
			          </div>
			        </div>
			      </div>
			    </div>
		);
	}
}

export default Register;
