import React from 'react';
import './signin.css';

class Signin extends React.Component {
	constructor(props) {
		super(props);
		this.handleErrorOff = this.handleErrorOff.bind(this);
		this.state = {
			signInEmail: '',
			signInPassword: '',
			signInError: false
		}
	}

	onEmailChange = (event) => {
		this.setState({signInEmail: event.target.value})
	}

	onPasswordChange = (event) => {
		this.setState({signInPassword: event.target.value})
	}

	onSubmitSignIn = () => {
		fetch('http://localhost:3000/signin', {
			method: 'post',
			headers: {'Content-Type': 'application/json'},
			body: JSON.stringify({
				email: this.state.signInEmail,
				password: this.state.signInPassword
			})
		})
		    .then(response => response.json())
		    .then(user => {
		  	  if(user.id) {
		  	  	this.handleErrorOff()
		  		this.props.loadUser(user)
		  		this.props.onRouteChange('home');
		  	  } else {
		  	  	this.handleErrorOn()
		  	  	// this.props.onRouteChange('signin');
		  	  }
		    })
	}

	handleErrorOn() {
		this.setState({signInError: true})
	}

	handleErrorOff() {
		this.setState({signInError: false})
	}

	render() {
	  const error = this.state.signInError;
	  const { onRouteChange } = this.props;
	  return (
	    <div className="cont_principal">
		  <div className="cont_centrar">
		  <div className="cont_login">
		    <div className="cont_tabs_login">
		      <ul className='ul_tabs'>
		        <li className="active"><p onClick={() => onRouteChange('signin') }>SIGN IN</p>
		        <span className="linea_bajo_nom"></span>
		        </li>
		        <li><p onClick= {() => onRouteChange('register') }>REGISTER</p><span className="linea_bajo_nom"></span>
		        </li>
		      </ul>
		      </div>
		  <div className="cont_text_inputs">
		    { !error (
			    <div className="error">
			    	Wrong email or password.
			    </div>
			    )
			}
		    <input 
		    	type="text" 
		    	className="input_form_sign d_block active_inp" 
		    	placeholder="EMAIL" 
		    	name="email_us" 
		    	onChange={this.onEmailChange}
		    />

		    <input 
		    	type="password" 
		    	className="input_form_sign d_block  active_inp" 
		    	placeholder="PASSWORD" 
		    	name="pass_us" 
		    	onChange={this.onPasswordChange}
		    />  

		    <p className="link_forgot_pass d_block" >Forgot Password ?</p>    
		<div className="terms_and_cons d_none">

		    </div>
		      </div>
		<div className="cont_btn">
		     <input className="btn_sign" onClick={this.onSubmitSignIn} type="submit" value="SIGN IN" />
		      </div>
		    </div>
		  </div>
		</div>
	  );
	}
}

export default Signin;
