import React from 'react';
import './adddealmodal.css';
import Select from 'react-select';
import {css} from 'emotion';

function validate(tenant, unit, property, llbroker, ttbroker) { 
  return {
    tenant: (tenant.length === 0),
    unit: (unit.length === 0),
    property: (property.length === 0),
    llbroker: (llbroker.length === 0),
    ttbroker: (ttbroker.length === 0),
  };
}

class AddDealModal extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      tenant: '',
      unit: '',
      property: '',
      llbroker: '',
      ttbroker: '',
      touched: {
        tenant: false,
        unit: false,
        property: false,
        llbroker: false,
        ttbroker: false
      },
      tenantDropdown: [],
      propertyDropdown: [],
      unitDropdown: [],
      llbrokerDropdown: [],
      ttbrokerDropdown: [],

    }
  }

  async componentDidMount() {
    let initTenantDropdown: [];
    let initPropertyDropdown: [];
    let initUnitDropdown: [];
    let initLLbrokerDropdown: [];
    let initTTbrokerDropdown: [];
    await fetch('http://localhost:3000/adddealmodal/tenant', {
      method: 'get',
      headers: {'Content-Type': 'application/json'},
    })
      .then(response => response.json())
      .then(tenants => {
        tenants.forEach(element => {
          let dropDownEle = { label: element["tenantname"], value: element };
          this.state.tenantDropdown.push(dropDownEle);
        });      
      })
  }

  onTenantChange = (value) => {
    this.setState({tenant: value})
    console.log(value);
  }

  onUnitChange = (event) => {
    this.setState({unit: event.target.value})
  }

  onPropertyChange = (event) => {
    this.setState({property: event.target.value})
  }

  onLLBrokerChange = (event) => {
    this.setState({llbroker: event.target.value})
  }

  onTTBrokerChange = (event) => {
    this.setState({ttbroker: event.target.value})
  }

  handleBlur = (field) => (event) => {
      this.setState({
        touched: { ...this.state.touched, [field]: true},
      });   
  }

  canBeSubmitted() {
    const errors = validate(this.state.tenant, this.state.property, this.state.unit, this.state.llbroker, this.state.ttbroker);
    const isDisabled = Object.keys(errors).some(x => errors[x]);
    return !isDisabled;
  }

  handleSubmit = (event) => {
    if (!this.canBeSubmitted()) {
      this.setState({
        touched: { ...this.state.touched, 'tenant': true, 'property': true, 'unit': true, 'llbroker': true, 'ttbroker': true}
      })
      event.preventDefault();
      return;

    }

    fetch('http://localhost:3000/dealsedit', {
      method: 'post',
      headers: {'Content-Type': 'application/json'},
      body: JSON.stringify({
        tenant: this.state.tenant,
        property: this.state.property,
        unit: this.state.unit,
        llbroker: this.state.llbroker,
        ttbroker: this.state.ttbroker,
      })
    })
      .then(response => response.json())
      .then(deal => {
        if(deal) {
          this.props.handleDealNoAdd();
        } else { console.log("uhoh")}
      })
    this.stateClear();
  }

  stateClear = () => {
    this.setState({
      touched: false,
      tenant: '',
      unit: '',
      property: '',
      llbroker: '',
      ttbroker: '',
    });
  }

  render() {
    const errors = validate(this.state.tenant, this.state.unit, this.state.property, this.state.llbroker, this.state.ttbroker);
    const isDisabled = Object.keys(errors).some(x => errors[x]);
    const shouldMarkError = (field) => {
      return errors[field] && this.state.touched[field];
    }

    const showHideClassName = this.props.show ? 'modal display-block' : 'modal display-none';
    return (
      <div className={showHideClassName}>
        <section className='modal-main'>
          <div className="modal-title">
            Add New Deal
          </div>
          <div className="modal-body">

            <p className={shouldMarkError('tenant') ? "input-title-error" : "input-title"}>Tenant Name:</p>
            <Select 
              options={this.state.tenantDropdown}
              className="form-input"
              onChange={this.onTenantChange.bind(this)}
              onBlur={this.handleBlur('tenant')}
            />

            <p className={shouldMarkError('property') ? "input-title-error" : "input-title"}>Property:</p>
            <Select 
              options={this.state.propertyDropdown}
              className="form-input"
              onChange={this.onPropertyChange.bind(this)}
              onBlur={this.handleBlur('property')}
            />

            <p className={shouldMarkError('unit') ? "input-title-error" : "input-title"}>Unit:</p>
            <Select 
              options={this.state.unitDropdown}
              className="form-input"
              onChange={this.onUnitChange.bind(this)}
              onBlur={this.handleBlur('unit')}
            />

            <p className={shouldMarkError('llbroker') ? "input-title-error" : "input-title"}>Landlord Broker:</p>
            <Select 
              options={this.state.llbrokerDropdown}
              className="form-input"
              onChange={this.onLLBrokerChange.bind(this)}
              onBlur={this.handleBlur('llbroker')}
            />

            <p className={shouldMarkError('ttbroker') ? "input-title-error" : "input-title"}>Tenant Broker:</p>
            <Select 
              options={this.state.ttbrokerDropdown}
              className="form-input"
              onChange={this.onTTBrokerChange.bind(this)}
              onBlur={this.handleBlur('ttbroker')}
            />
            <div className="line"></div>
            <div className="buttons">

            { isDisabled ? (
              <input 
                type="submit" 
                value="ADD DEAL" 
                className="button add-button-error"
                onClick={ (event) => this.handleSubmit(event)}
              />
            ) : (
              <input 
                type="submit" 
                value="ADD DEAL" 
                className="button add-button"
                onClick={ (event) => this.handleSubmit(event)}
              />
            )}

            <input
              type="cancel"
              value="CANCEL"
              className="button cancel-button"
              onClick={ (event) => { this.props.handleDealNoAdd(); this.stateClear(); }}
            />

            </div>
          </div>
        </section>
      </div>
    );
  }
};

export default AddDealModal;
