import React from 'react';
import './dealedit.css';

class DealEdit extends React.Component {
	constructor(props) {
		super(props);

		this.state = {
			isLoading: true,
			deal: [],
			dealSummaryEdit: false,
		}
	}

	loadDeal() {
		const fetchURL = `http://localhost:3000/deals/14`; /* ${this.props.dealid} */
		fetch(fetchURL, {
			method: 'get',
			headers: {'Content-Type': 'application/json'},
		})
		    .then(response => response.json())
		    .then(deal => {
		  	  	this.setState({ 
		  	  		isLoading: false,
		  	  		deal: deal,
		  	  	})
		    })
	}

	componentDidMount() {
		this.loadDeal();
	}

	onTenantChange = (event) => {
		this.setState({tenant: event.target.value})
	}

	onPropertyChange = (event) => {
		this.setState({property: event.target.value})
	}

	onUnitChange = (event) => {
		this.setState({unit: event.target.value})
	}


	handleSubmit = () => {
		fetch('http://localhost:3000/dealsedit', {
			method: 'post',
			headers: {'Content-Type': 'application/json'},
			body: JSON.stringify({
				tenant: this.state.tenant,
				property: this.state.property,
				unit: this.state.unit,
			})
		})
		  .then(response => response.json())
		  .then(deal => {
		  	if(deal.id) {
		  		this.props.handleDealNoAdd();
		  	} else { console.log("uhoh")}
		  })
	}

	render() {
		const { dealid } = this.props;
		return (
			<div className="container">
			  { !this.state.isLoading ? ( 
				  <React.Fragment>
				  <div className="left-col">
				    <div className="deal-summary">
				      <div className="innerbox float-left">
				        <p className="boxtitle">DEAL SUMMARY</p>
						  <div className="table">
							<table>
								<tbody>
									<tr>
										<td className="float-left margin-right">Tenant Name: </td>
										<td className="float-left"> 			
										  { this.state.dealSummaryEdit ? (      
											<input 
										    	type="text" 
										    	className="inputbox" 
										    	placeholder={this.state.deal[0].tenant} 
										    	name="property" 
										    	value={this.state.tenant}
										    	onChange={this.onTenantChange}
										    />  
										    ) : ( this.state.deal[0].tenant )
										  }
										</td>
									</tr>
									<tr>
										<td className="float-left margin-right">Property: </td>
										<td> 				      
											<input 
										    	type="text" 
										    	className="inputbox" 
										    	placeholder={this.state.deal[0].property} 
										    	name="property" 
										    	value={this.state.property}
										    	onChange={this.onPropertyChange}
										      />  
									      </td>
									</tr>
									<tr>
										<td className="float-left margin-right">Unit: </td>
										<td> 				      
											<input 
										    	type="text" 
										    	className="inputbox" 
										    	placeholder={this.state.deal[0].unit} 
										    	name="unit" 
										    	value={this.state.unit}
										    	onChange={this.onUnitChange}
										      />  
									      </td>
									</tr>
									<tr>
										<td className="float-left margin-right">Landlord Broker: </td>
										<td> 				      
											<input 
										    	type="text" 
										    	className="inputbox" 
										    	placeholder={this.state.deal[0].llbroker} 
										    	name="llbroker" 
										    	value={this.state.llbroker}
										    	onChange={this.onLLBrokerChange}
										      />  
									      </td>
									</tr>
									<tr>
										<td className="float-left margin-right">Tenant Broker: </td>
										<td> 				      
											<input 
										    	type="text" 
										    	className="inputbox" 
										    	placeholder={this.state.deal[0].ttbroker} 
										    	name="property" 
										    	value={this.state.ttbroker}
										    	onChange={this.onTTBrokerChange}
										      />  
									      </td>
									</tr>
									<tr>
										<td className="float-left margin-right">Date Added: </td>
										<td> 				      
											<input 
										    	type="text" 
										    	className="inputbox" 
										    	placeholder={this.state.deal[0].creationdate} 
										    	name="creationdate" 
										    	value={this.state.creationdate}
										      />  
									      </td>
									</tr>
									<tr>
										<td className="float-left margin-right">Deal Type: </td>
										<td> 				      
											<input 
										    	type="text" 
										    	className="inputbox" 
										    	placeholder={this.state.deal[0].type} 
										    	name="property" 
										    	value={this.state.type}
										    	onChange={this.onTypeChange}
										      />  
									      </td>
									</tr>

								</tbody>
							</table>
						</div>


				      <p><input
				    	type="button"
				    	className="plugbutton"
				    	placeholder="Add Deal"
				    	name="add"
				    	onClick= {this.handleSubmit}
				      /></p>
				      <p><input
				    	type="button"
				    	className="plugbutton"
				    	placeholder="Cancel"
				    	name="cancel"
				    	onClick= {this.props.handleDealNoAdd}
				      /></p>
				     </div>
				     <div className="innerbox">
						<div className="latest-terms">
							<table>
								<tbody>
									<tr>
										<td className="float-left margin-right">LATEST TERMS DROPDOWN </td>
										<td> 			      

										</td>
									</tr>
